create table ES_Send_Log( Id int primary key identity(1,1), Booking_Id int null, Report_Id nvarchar(2500), Audit_Id int null, To_Recipient nvarchar(1500), CC_Recipient nvarchar(1500), Subject nvarchar(2500), Body nvarchar(max), Attachment nvarchar(max), Created_By int null, Created_On DATETIME NOT NULL DEFAULT GETDATE(), Is_Send bit, Try_Count int, Report_Links nvarchar(max), Es_Details_Id int NOT NULL CONSTRAINT FK_ES_Send_Log_Created_By FOREIGN KEY (Created_By) REFERENCES [dbo].[IT_UserMaster](Id),	 CONSTRAINT FK_ES_Send_Log_Booking_Id FOREIGN KEY (Booking_Id) REFERENCES [dbo].[Insp_transaction](Id), CONSTRAINT FK_ES_Send_Log_Auidt_Id FOREIGN KEY (Audit_Id) REFERENCES [dbo].[AUD_Transaction](Id), CONSTRAINT FK_ES_Send_Log_Es_Details_Id FOREIGN KEY (Es_Details_Id) REFERENCES [dbo].[ES_Details](Id) )